beans:entry.beans:value:
prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix owl: <http://www.w3.org/2002/07/owl#> prefix xsd: <http://www.w3.org/2001/XMLSchema#> prefix sn: <http://snomed.info/sct/term/>
CONSTRUCT { ?s ?prop ?o. ?s sn:description ?desc. ?desc ?dProp ?dO.
} WHERE { bind(%s as ?s) ?s ?prop ?o. ?s sn:description ?desc. ?desc ?dProp ?dO. }

prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix owl: <http://www.w3.org/2002/07/owl#> prefix xsd: <http://www.w3.org/2001/XMLSchema#> prefix sn: <http://snomed.info/sct/term/>
SELECT ?effectiveTime
WHERE { bind(%s as ?s) ?s sn:effectiveTime ?effectiveTime. }

prefix rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#> prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#> prefix owl: <http://www.w3.org/2002/07/owl#> prefix xsd: <http://www.w3.org/2001/XMLSchema#> prefix sn: <http://snomed.info/sct/term/>
CONSTRUCT { ?s sn:status ?status. ?status ?p ?o. } WHERE {
bind(%s as ?s) ?s sn:status ?status. ?status ?p ?o.
}
